import React from 'react';

class AppClass extends React.Component{
    time = "";
    render(){
        return(
            <div>
                <button className="title" onClick={this.titleClick}>My Time</button>
                <h1>Time: {this.time}</h1>
            </div>
        )
    }
    titleClick = () => {
        let data = new Date();
        this.time = data.getHours() + ':' + data.getMinutes();
    } 
}

export default AppClass
